sp_widget.client_script:
api.controller=function() {
  /* widget controller */
  var c = this;

	//reacion nn-click (imprime en consola)
	c.selectItem = function(idx) {
    var id = c.data.notes[idx].sys_id;// guarda el sysid
    console.log('Note ID: ' + id);//lo inprime ne consola

    $rootScope.noteID = id;//lo retorna para ser usado 
    $rootScope.$emit('selectNote', id);//.emit emite informacion para que en
		//otro widget reciba informacion con .on
  }




};

sp_widget.script:
(function() {
	//mostar informacion de tabla que se necesita.
//create an array to populate with notes
data.notes = [];
var noteGR = new GlideRecord('x_snc_createnotes_note');
//noteGR.addQuery('user', gs.getUser().getID());//registros creados solo con un usuario
noteGR.orderByDesc('sys_created_on');//ordena de forma descendente
noteGR.query();

while (noteGR.next()) {//recorre todos losreguistros de la consulta.
var noteObj = {};
//use service portal helper method to get some display values
//utilice el método auxiliar del portal de servicios para obtener algunos valores de visualización
$sp.getRecordDisplayValues(noteObj, noteGR, 'number,title,sys_id'); //valores de visualización
//get the first 20 characters of the description
//obtiene los primeros 20 caracteres de la descripción
noteObj.note = noteGR.getValue('note').slice(0,20);
//push the populated obj into the array
//empujar el obj poblado en la matriz
data.notes.push(noteObj);
}

	//termina consulta de tabla


})();
